document.addEventListener('DOMContentLoaded', function() { console.log('Newsletter script loaded - simple styling only'); const newsletterInputs = document.querySelectorAll('.newsletter-form input'); newsletterInputs.forEach(input => { input.addEventListener('focus', function() { this.classList.add('focused'); }); input.addEventListener('blur', function() { this.classList.remove('focused'); if (this.checkValidity()) { this.classList.add('valid'); this.classList.remove('invalid'); } else if (this.value) { this.classList.add('invalid'); this.classList.remove('valid'); } }); }); });